        return {
            type: 'Feature',
            geometry: {
                type: "Point",
                //coordinates: [2.3508, 48.8567]
                coordinates: [post.position.lng, post.position.lat]
            },
            properties: {
                "id": post._id,
                "opacity": post.opacity,
                "order": post.viewId
            }
        };

